INSERT INTO `v9_page` VALUES ('5', '商家加盟', ';', '商家 加盟', '　　成都博大教育科技有限公司(Chengdu&nbsp;Broad&nbsp;Education&nbsp;&amp;&nbsp;Technology&nbsp;Co.,td.)总部坐落在四川省成都市高新技术产业开发区，公司创立于2001年，是为了顺应原国家人事部1990年9月以来在全国推行职称考试，专门从事职称计算机考试、职称英语考试、职业资格考试和各类公务员考试软件研究、开发、设计、生产和销售为一体的专业教育机构，是国内职业教育市场上最早进入家庭式教育培训的企业之一。公司多年来一直秉承&ldquo;勤学精进、明德笃志、重责守诺、乐助共赢&rdquo;的企业文化，致力于打造不受时间和地域限制的家庭式教育模式，旨在帮助各类在职人士通过各种专业职称考试，实现职业晋升梦想。经过十多年的探索和发展，博大教育目前已形成博大考神职称计算机考试系列、博大考神职称英语考试家庭式培训班、博大弘什公务员考试培训班和博大致睿职业资格考试系列等四大系列十多个种类产品，受到了各类培训考试机构和广大考生的极力追捧，目前已是国内职业教育考试培训行业的领先品牌。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n<span style=\"font-size: 14px\"><strong>　　招商咨询热线：028-66820003<br />\r\n<br />\r\n　　联系QQ: 1450223039<br />\r\n<br />\r\n　　Email:&nbsp;1450223039@qq.com</strong></span><br />\r\n<br />\r\n　　博大教育官方网站：<a href=\"http://www.cdbroad.com/\">http://www.cdbroad.com</a><br />\r\n<br />\r\n　　博大教育旗下网站：&nbsp;<br />\r\n<br />\r\n　　博大考神职称考试网&nbsp;<a href=\"http://www.kaozc.com/\">http://www.kaozc.com</a>&nbsp;<br />\r\n<br />\r\n　　博大弘仕国家公务员考试网&nbsp;<a href=\"http://www.kaogwy.com/\">http://www.kaogwy.com</a><br />\r\n<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;　<br />\r\n　　<span style=\"font-size: 16px\"><a href=\"http://www.enbroad.com/UploadedPic/liyueming//网络经销商申请表.doc\">附件：网络经销商申请表.doc&nbsp;</a></span><br />\r\n<br />\r\n　　', '', '0');
