INSERT INTO `v9_pinlun` VALUES ('461', '很好用！从来不知道有那么划算的东西！考试轻松就过关了！非常好的宝贝，会推荐给同事的，满分好评', '9', '1');
INSERT INTO `v9_pinlun` VALUES ('462', '用了一阵子才来评价的，东东还不错，售后服务也好，如果严格按老师要求做过关是没问题的。希望自己能过吧', '9', '1');
INSERT INTO `v9_pinlun` VALUES ('463', '博大考神职称英语家庭式培训班视频教学很容易就理解了，把词典带进考场按照老师教授的技巧查词，明显感觉自己比同考场的人做题速度快，考试真的变得很容易', '9', '1');
